INSERT INTO `inside_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES 
(1, 0, 0, 'Ace', 'Ace code editor plugin for MODx Revolution', 0, 0, 0, '/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == \'OnRichTextEditorRegister\') {\n    $modx->event->output(\'Ace\');\n    return;\n}\n\nif ($modx->getOption(\'which_element_editor\', null, \'Ace\') !== \'Ace\') {\n    return;\n}\n\n$ace = $modx->getService(\'ace\', \'Ace\', $modx->getOption(\'ace.core_path\', null, $modx->getOption(\'core_path\').\'components/ace/\').\'model/ace/\');\n$ace->initialize();\n\n$extensionMap = array(\n    \'tpl\'   => \'text/x-smarty\',\n    \'htm\'   => \'text/html\',\n    \'html\'  => \'text/html\',\n    \'css\'   => \'text/css\',\n    \'scss\'  => \'text/x-scss\',\n    \'less\'  => \'text/x-less\',\n    \'svg\'   => \'image/svg+xml\',\n    \'xml\'   => \'application/xml\',\n    \'xsl\'   => \'application/xml\',\n    \'js\'    => \'application/javascript\',\n    \'json\'  => \'application/json\',\n    \'php\'   => \'application/x-php\',\n    \'sql\'   => \'text/x-sql\',\n    \'md\'    => \'text/x-markdown\',\n    \'txt\'   => \'text/plain\',\n    \'twig\'  => \'text/x-twig\'\n);\n\n// Define default mime for html elements(templates, chunks and html resources)\n$html_elements_mime=$modx->getOption(\'ace.html_elements_mime\',null,false);\nif(!$html_elements_mime){\n    // this may deprecated in future because components may set ace.html_elements_mime option now\n    switch (true) {\n        case $modx->getOption(\'twiggy_class\'):\n            $html_elements_mime = \'text/x-twig\';\n            break;\n        case $modx->getOption(\'pdotools_fenom_parser\'):\n            $html_elements_mime = \'text/x-smarty\';\n            break;\n        default:\n            $html_elements_mime = \'text/html\';\n    }\n}\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case \'OnSnipFormPrerender\':\n        $field = \'modx-snippet-snippet\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnTempFormPrerender\':\n        $field = \'modx-template-content\';\n        $modxTags = true;\n        $mimeType = $html_elements_mime;\n        break;\n    case \'OnChunkFormPrerender\':\n        $field = \'modx-chunk-snippet\';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->name, PATHINFO_EXTENSION);\n            if(!$extension||!isset($extensionMap[$extension])){\n                $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            }\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : \'text/plain\';\n        } else {\n            $mimeType = $html_elements_mime;\n        }\n        $modxTags = true;\n        break;\n    case \'OnPluginFormPrerender\':\n        $field = \'modx-plugin-plugincode\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnFileCreateFormPrerender\':\n        $field = \'modx-file-content\';\n        $mimeType = \'text/plain\';\n        break;\n    case \'OnFileEditFormPrerender\':\n        $field = \'modx-file-content\';\n        $extension = pathinfo($scriptProperties[\'file\'], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : \'text/plain\';\n        $modxTags = $extension == \'tpl\';\n        break;\n    case \'OnDocFormPrerender\':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = \'ta\';\n        $mimeType = $modx->getObject(\'modContentType\', $modx->controller->resourceArray[\'content_type\'])->get(\'mime_type\');\n\n        if($mimeType == \'text/html\')$mimeType = $html_elements_mime;\n\n        if ($modx->getOption(\'use_editor\')){\n            $richText = $modx->controller->resourceArray[\'richtext\'];\n            $classKey = $modx->controller->resourceArray[\'class_key\'];\n            if ($richText || in_array($classKey, array(\'modStaticResource\',\'modSymLink\',\'modWebLink\',\'modXMLRPCResource\'))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = \'\';\nif ($field) {\n    $script .= \"MODx.ux.Ace.replaceComponent(\'$field\', \'$mimeType\', $modxTags);\";\n}\n\nif ($modx->event->name == \'OnDocFormPrerender\' && !$modx->getOption(\'use_editor\')) {\n    $script .= \"MODx.ux.Ace.replaceTextAreas(Ext.query(\'.modx-richtext\'));\";\n}\n\nif ($script) {\n    $modx->controller->addHtml(\'<script>Ext.onReady(function() {\' . $script . \'});</script>\');\n}', 0, 'a:0:{}', 0, '', 0, 'ace/elements/plugins/ace.plugin.php'),
(2, 1, 0, 'AutoTemplate', 'Set the template of a new resource automatically to the one it\'s siblings have or, if it has no siblings, to the one it\'s parent has.', 0, 4, 0, '/**\n * AutoTemplate for MODx Revolution\n *\n * This plugin sets a new document\'s template automatically to the one it\'s siblings\n * have or, if it has no siblings, to the one it\'s parent has.\n *\n * AutoTemplate is free software; you can redistribute it and/or modify it under\n * the terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * AutoTemplate is distributed in the hope that it will be useful, but WITHOUT\n * ANY WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS\n * FOR A PARTICULAR PURPOSE. See the GNU General Public License for more\n * details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Inherit Template; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @author      Maarten     <@maarten>\n * @copyright   Copyright(c)2009,Magnatron\n * @license     GPL v2\n *\n * @package     AutoTemplate\n * @subpackage  plugin\n */\n\n\nif ($modx->event->name === \'OnDocFormRender\') {\n\t\n\t// Only when new\n\tif (empty($scriptProperties[\'mode\']) || $scriptProperties[\'mode\'] !== \'new\') return;\n\t\n\t// Siblings\n\t$c = $modx->newQuery(\'modResource\', array(\'parent\'=>$_REQUEST[\'parent\']));\n\t$c->sortby(\'id\', \'desc\'); // Reference last added sibling\n\t$siblings = $modx->getCollection(\'modResource\', $c);\n\tif(count($siblings)>0){\n\t\t$bro = array_shift($siblings);\n\t\t$modx->controller->setProperty(\'template\', $bro->get(\'template\'));\n\t\treturn;\n\t}\n\t// No siblings, use parent\n\tif((int)$_REQUEST[\'parent\']!=0){\n\t\t$dad = $modx->getObject(\'modResource\', $_REQUEST[\'parent\']);\n\t\t$modx->controller->setProperty(\'template\', $dad->get(\'template\'));\n\t\treturn;\n\t}\n}\n\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(44, 0, 0, 'Collections', '', 0, 106, 0, '/**\n * Collections\n *\n * DESCRIPTION\n *\n * This plugin inject JS to handle proper working of close buttons in Resource\'s panel (OnDocFormPrerender)\n * This plugin handles setting proper show_in_tree parameter (OnBeforeDocFormSave, OnResourceSort)\n *\n * @var modX $modx\n * @var array $scriptProperties\n */\n$corePath = $modx->getOption(\'collections.core_path\', null, $modx->getOption(\'core_path\', null, MODX_CORE_PATH) . \'components/collections/\');\n/** @var Collections $collections */\n$collections = $modx->getService(\n    \'collections\',\n    \'Collections\',\n    $corePath . \'model/collections/\',\n    array(\n        \'core_path\' => $corePath\n    )\n);\n\n$className = \'Collections\' . $modx->event->name;\n\n$modx->loadClass(\'CollectionsPlugin\', $collections->getOption(\'modelPath\') . \'collections/events/\', true, true);\n$modx->loadClass($className, $collections->getOption(\'modelPath\') . \'collections/events/\', true, true);\n\nif (class_exists($className)) {\n    /** @var CollectionsPlugin $handler */\n    $handler = new $className($modx, $scriptProperties);\n    $handler->run();\n}\n\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(40, 0, 0, 'MIGX', '', 0, 105, 0, '$corePath = $modx->getOption(\'migx.core_path\',null,$modx->getOption(\'core_path\').\'components/migx/\');\n$assetsUrl = $modx->getOption(\'migx.assets_url\', null, $modx->getOption(\'assets_url\') . \'components/migx/\');\nswitch ($modx->event->name) {\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/input/\');\n        break;\n    case \'OnTVInputPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/inputoptions/\');\n        break;\n\n        case \'OnDocFormPrerender\':\n        $modx->controller->addCss($assetsUrl.\'css/mgr.css\');\n        break; \n \n    /*          \n    case \'OnTVOutputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/output/\');\n        break;\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/properties/\');\n        break;\n    \n    case \'OnDocFormPrerender\':\n        $assetsUrl = $modx->getOption(\'colorpicker.assets_url\',null,$modx->getOption(\'assets_url\').\'components/colorpicker/\'); \n        $modx->regClientStartupHTMLBlock(\'<script type=\"text/javascript\">\n        Ext.onReady(function() {\n            \n        });\n        </script>\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorPicker.js\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorMenu.js\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorPickerField.js\');\t\t\n        $modx->regClientCSS($assetsUrl.\'resources/css/colorpicker.css\');\n        break;\n     */\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(41, 0, 0, 'MIGXquip', '', 0, 105, 0, '$quipCorePath = $modx->getOption(\'quip.core_path\', null, $modx->getOption(\'core_path\') . \'components/quip/\');\n//$assetsUrl = $modx->getOption(\'migx.assets_url\', null, $modx->getOption(\'assets_url\') . \'components/migx/\');\nswitch ($modx->event->name)\n{\n\n    case \'OnDocFormPrerender\':\n\n        \n        require_once $quipCorePath . \'model/quip/quip.class.php\';\n        $modx->quip = new Quip($modx);\n\n        $modx->lexicon->load(\'quip:default\');\n        $quipconfig = $modx->toJson($modx->quip->config);\n        \n        $js = \"\n        Quip.config = Ext.util.JSON.decode(\'{$quipconfig}\');\n        console.log(Quip);\";\n\n        //$modx->controller->addCss($assetsUrl . \'css/mgr.css\');\n        $modx->controller->addJavascript($modx->quip->config[\'jsUrl\'].\'quip.js\');\n        $modx->controller->addHtml(\'<script type=\"text/javascript\">\' . $js . \'</script>\');\n        break;\n\n}\nreturn;', 0, 'a:0:{}', 1, '', 0, ''),
(42, 0, 0, 'migxResizeOnUpload', '', 0, 105, 0, '/**\n * migxResizeOnUpload Plugin\n *\n * Events: OnFileManagerUpload\n * Author: Bruno Perner <b.perner@gmx.de>\n * Modified to read multiple configs from mediasource-property\n * \n * First Author: Vasiliy Naumkin <bezumkin@yandex.ru>\n * Required: PhpThumbOf snippet for resizing images\n * \n * Example: mediasource - property \'resizeConfig\':\n * [{\"alias\":\"origin\",\"w\":\"500\",\"h\":\"500\",\"far\":1},{\"alias\":\"thumb\",\"w\":\"150\",\"h\":\"150\",\"far\":1}]\n */\n\nif ($modx->event->name != \'OnFileManagerUpload\') {\n    return;\n}\n\n\n$file = $modx->event->params[\'files\'][\'file\'];\n$directory = $modx->event->params[\'directory\'];\n\nif ($file[\'error\'] != 0) {\n    return;\n}\n\n$name = $file[\'name\'];\n//$extensions = explode(\',\', $modx->getOption(\'upload_images\'));\n\n$source = $modx->event->params[\'source\'];\n\nif ($source instanceof modMediaSource) {\n    //$dirTree = $modx->getOption(\'dirtree\', $_REQUEST, \'\');\n    //$modx->setPlaceholder(\'docid\', $resource_id);\n    $source->initialize();\n    $basePath = str_replace(\'/./\', \'/\', $source->getBasePath());\n    //$cachepath = $cachepath . $dirTree;\n    $baseUrl = $modx->getOption(\'site_url\') . $source->getBaseUrl();\n    //$baseUrl = $baseUrl . $dirTree;\n    $sourceProperties = $source->getPropertyList();\n\n    //echo \'<pre>\' . print_r($sourceProperties, 1) . \'</pre>\';\n    //$allowedExtensions = $modx->getOption(\'allowedFileTypes\', $sourceProperties, \'\');\n    //$allowedExtensions = empty($allowedExtensions) ? \'jpg,jpeg,png,gif\' : $allowedExtensions;\n    //$maxFilesizeMb = $modx->getOption(\'maxFilesizeMb\', $sourceProperties, \'8\');\n    //$maxFiles = $modx->getOption(\'maxFiles\', $sourceProperties, \'0\');\n    //$thumbX = $modx->getOption(\'thumbX\', $sourceProperties, \'100\');\n    //$thumbY = $modx->getOption(\'thumbY\', $sourceProperties, \'100\');\n    $resizeConfigs = $modx->getOption(\'resizeConfigs\', $sourceProperties, \'\');\n    $resizeConfigs = $modx->fromJson($resizeConfigs);\n    $thumbscontainer = $modx->getOption(\'thumbscontainer\', $sourceProperties, \'thumbs/\');\n    $imageExtensions = $modx->getOption(\'imageExtensions\', $sourceProperties, \'jpg,jpeg,png,gif,JPG\');\n    $imageExtensions = explode(\',\', $imageExtensions);\n    //$uniqueFilenames = $modx->getOption(\'uniqueFilenames\', $sourceProperties, false);\n    //$onImageUpload = $modx->getOption(\'onImageUpload\', $sourceProperties, \'\');\n    //$onImageRemove = $modx->getOption(\'onImageRemove\', $sourceProperties, \'\');\n    $cleanalias = $modx->getOption(\'cleanFilename\', $sourceProperties, false);\n\n}\n\nif (is_array($resizeConfigs) && count($resizeConfigs) > 0) {\n    foreach ($resizeConfigs as $rc) {\n        if (isset($rc[\'alias\'])) {\n            $filePath = $basePath . $directory;\n            $filePath = str_replace(\'//\',\'/\',$filePath);\n            if ($rc[\'alias\'] == \'origin\') {\n                $thumbPath = $filePath;\n            } else {\n                $thumbPath = $filePath . $rc[\'alias\'] . \'/\';\n                $permissions = octdec(\'0\' . (int)($modx->getOption(\'new_folder_permissions\', null, \'755\', true)));\n                if (!@mkdir($thumbPath, $permissions, true)) {\n                    $modx->log(MODX_LOG_LEVEL_ERROR, sprintf(\'[migxResourceMediaPath]: could not create directory %s).\', $thumbPath));\n                } else {\n                    chmod($thumbPath, $permissions);\n                }\n\n            }\n\n\n            $filename = $filePath . $name;\n            $thumbname = $thumbPath . $name;\n            $ext = substr(strrchr($name, \'.\'), 1);\n            if (in_array($ext, $imageExtensions)) {\n                $sizes = getimagesize($filename);\n                echo $sizes[0]; \n                //$format = substr($sizes[\'mime\'], 6);\n                if ($sizes[0] > $rc[\'w\'] || $sizes[1] > $rc[\'h\']) {\n                    if ($sizes[0] < $rc[\'w\']) {\n                        $rc[\'w\'] = $sizes[0];\n                    }\n                    if ($sizes[1] < $rc[\'h\']) {\n                        $rc[\'h\'] = $sizes[1];\n                    }\n                    $type = $sizes[0] > $sizes[1] ? \'landscape\' : \'portrait\';\n                    if (isset($rc[\'far\']) && $rc[\'far\'] == \'1\' && isset($rc[\'w\']) && isset($rc[\'h\'])) {\n                        if ($type = \'landscape\') {\n                            unset($rc[\'h\']);\n                        }else {\n                            unset($rc[\'w\']);\n                        }\n                    }\n\n                    $options = \'\';\n                    foreach ($rc as $k => $v) {\n                        if ($k != \'alias\') {\n                            $options .= \'&\' . $k . \'=\' . $v;\n                        }\n                    }\n                    $resized = $modx->runSnippet(\'phpthumbof\', array(\'input\' => $filePath . $name, \'options\' => $options));\n                    rename(MODX_BASE_PATH . substr($resized, 1), $thumbname);\n                }\n            }\n\n\n        }\n    }\n}', 0, 'a:0:{}', 0, '', 0, ''),
(11, 1, 0, 'pdoTools', '', 0, 9, 0, '/** @var modX $modx */\nswitch ($modx->event->name) {\n\n    case \'OnMODXInit\':\n        $fqn = $modx->getOption(\'pdoTools.class\', null, \'pdotools.pdotools\', true);\n        $path = $modx->getOption(\'pdotools_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\n        $modx->loadClass($fqn, $path, false, true);\n\n        $fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\n        $path = $modx->getOption(\'pdofetch_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\n        $modx->loadClass($fqn, $path, false, true);\n        break;\n\n    case \'OnSiteRefresh\':\n        /** @var pdoTools $pdoTools */\n        if ($pdoTools = $modx->getService(\'pdoTools\')) {\n            if ($pdoTools->clearFileCache()) {\n                $modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(\'refresh_default\') . \': pdoTools\');\n            }\n        }\n        break;\n\n    case \'OnWebPagePrerender\':\n        $parser = $modx->getParser();\n        if ($parser instanceof pdoParser) {\n            foreach ($parser->pdoTools->ignores as $key => $val) {\n                $modx->resource->_output = str_replace($key, $val, $modx->resource->_output);\n            }\n        }\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/pdotools/elements/plugins/plugin.pdotools.php'),
(14, 1, 0, 'switchContext', '', 0, 0, 0, '// $lankey = substr($_SERVER[\'REQUEST_URI\'], 1, 2);\n$domain = $_SERVER[\'SERVER_NAME\'];\n\nif($modx->context->get(\'key\') != \"mgr\"){\n    switch ($domain) {\n        // case \'wsp-brand.indyn.de\':\n        //     $modx->switchContext(\'brand\');\n        //     $modx->setOption(\'cultureKey\', \'de\');\n        //     break;\n        // case \'wsp.indyn.de\':\n        default:\n            $modx->switchContext(\'web\');\n            $modx->setOption(\'cultureKey\', \'de\');\n            break;\n    }\n}\n$modx->lexicon->load(\'indyn:default\');\n$modx->lexicon->load(\'indyn:portfolio\');', 0, 'a:0:{}', 0, '', 0, ''),
(29, 0, 0, 'TinyMCERTE', '', 0, 48, 0, '/**\n * TinyMCE Rich Tech Editor\n *\n */\n$corePath = $modx->getOption(\'tinymcerte.core_path\', null, $modx->getOption(\'core_path\', null, MODX_CORE_PATH) . \'components/tinymcerte/\');\n/** @var TinyMCERTE $tinymcerte */\n$tinymcerte = $modx->getService(\n    \'tinymcerte\',\n    \'TinyMCERTE\',\n    $corePath . \'model/tinymcerte/\',\n    array(\n        \'core_path\' => $corePath\n    )\n);\n\n$className = \'TinyMCERTE\' . $modx->event->name;\n$modx->loadClass(\'TinyMCERTEPlugin\', $tinymcerte->getOption(\'modelPath\') . \'tinymcerte/events/\', true, true);\n$modx->loadClass($className, $tinymcerte->getOption(\'modelPath\') . \'tinymcerte/events/\', true, true);\nif (class_exists($className)) {\n    /** @var TinyMCERTEPlugin $handler */\n    $handler = new $className($modx, $scriptProperties);\n    $handler->run();\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(30, 0, 0, 'elRTE', 'elRTE and elFinder for MODx', 0, 0, 0, '/**\n*elRTE HTML Editor MODx Integration\n*Fires:\n*\t- OnRichTextBrowserInit\n*\t- OnRichTextEditorInit\n*\t- OnRichTextEditorRegister\n* integrated by CADCoder (http://www.cadcoder.com/)\n*/\n\t//Rich text registration into MODx, no need to load the editor\n\tif ($modx->event->name == \'OnRichTextEditorRegister\') {\n\t\t$modx->event->output(\'elRTE\');\n\t\treturn;\n\t}\n\tswitch ($modx->event->name) {\n\t\t//Text Editor Initialisation, no need to use for elRTE\n\t\tcase \"OnRichTextBrowserInit\":\t\t\t\n\t\tbreak;\n\t\t//Rich Text Editor Load\n\t\tcase \"OnRichTextEditorInit\":\n\t\t\tif($editor == \"elRTE\") {\n\t\n\t\t\t\t//Build the output for all css styling and Javascript modules\n\t\t\t\t$output = \'\t\t     \n\t\t\t\t\t<script src=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'js/jquery.min.js\" type=\"text/javascript\" charset=\"utf-8\"></script>\n\t\t\t\t\t<script src=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'js/jquery-ui-1.8.13.custom.min.js\" type=\"text/javascript\" charset=\"utf-8\"></script>\n\t\t\t\t\t<link rel=\"stylesheet\" href=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'css/smoothness/jquery-ui-1.8.13.custom.css\" type=\"text/css\" media=\"screen\" charset=\"utf-8\">\n\t\t\t\t\t<script src=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'js/elrte.full.js\" type=\"text/javascript\" charset=\"utf-8\"></script>\n\t\t\t\t\t<link rel=\"stylesheet\" href=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'css/elrte.full.css\" type=\"text/css\" media=\"screen\" charset=\"utf-8\">\n\t\t\t\t\t<script src=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'js/elfinder.min.js\" type=\"text/javascript\" charset=\"utf-8\"></script>\n\t\t\t\t\t<link rel=\"stylesheet\" href=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'css/elfinder.min.css\" type=\"text/css\" media=\"screen\" charset=\"utf-8\">\n\t\t\t  \t\t<link rel=\"stylesheet\" href=\"\'. $modx->getOption(\'site_url\').\'core/components/elrte/\'.\'css/theme.css\" type=\"text/css\" media=\"screen\" charset=\"utf-8\">\';\n\t\t\t\t//Inject the scripts and stylesheets into the manager head tags\n\t\t\t\t$modx->regClientStartupHTMLBlock($output);\n\n\t\t\t\t//MODx.on(\'ready\'...Ensures the script is run when MODx has finished loading. This is vital to ensure the \'ta\' textarea is loaded\n\t\t\t\t$output = \'\n\t\t\t\t\t<script type=\"text/javascript\" charset=\"utf-8\">\n\t\t\t\t\t\tvar opts;\n\t\t\t\t\t\tMODx.on(\\\'ready\\\',function() {\';\n\t\t\t\t\t\t//Check the toolbar configuration, if Custom, ensure it\'s loaded with the specified tools\n\t\t\t\t\t\tif ( $modx->getOption(\'toolbarMode\',$scriptProperties,\"custom\") == \"custom\")\n\t\t\t\t\t\t{\n\t\t\t\t\t\t\t$output = $output . \'elRTE.prototype.options.panels.customtools = [ \"\' .\n\t\t\t\t\t\t\timplode(\'\",\"\' ,explode(\",\", $modx->getOption(\'toolbar\',$scriptProperties,\"\")))\n\t\t\t\t\t\t\t. \'\"];\';\n\n\t\t\t\t\t\t  \tif ( $modx->getOption(\'plugins\',$scriptProperties,\'\') != \'\'){\n\t\t\t\t\t\t\t  $plugins = implode(\'\",\"\' ,explode(\",\", $modx->getOption(\'plugins\',$scriptProperties,\"\")));\n\t\t\t\t\t\t\t  $output = $output . \'elRTE.prototype.options.toolbars.custom = [\"customtools\", \"\' . $plugins . \'\"];\';\n\t\t\t\t\t\t  \t}else{\n\t\t\t\t\t\t    \t\t$output = $output . \'elRTE.prototype.options.toolbars.custom = [\"customtools\",];\';\n\t\t\t\t\t\t  \t}\n\t\t\t\t\t\t}\n\t\t\t\t\t\t//Append the toolbar configuration to the output\n\t\t\t\t\t\t$output = $output . \'opts = {height: 450, toolbar: \\\'\'. $modx->getOption(\'toolbarMode\',$scriptProperties,\"Custom\")  .\'\\\',\';\n\t\t\t\t\t\t//If a CSS file for the editor is specified, add it to the configuration (NOTE: CSS file is taken from document root)\n\t\t\t\t\t\tif ($modx->getOption(\'editor_css_path\'))\n\t\t\t\t\t\t{\n\t\t\t\t\t\t\t$output = $output . \'cssfiles : [\\\'\' . $modx->getOption(\'editor_css_path\') . \'\\\', ],\';\n\t\t\t\t\t\t}\n\t\t\t\t\t\t//Append the file manager segment and end the $output variable\n\t\t\t\t\t\t$output = $output . \'\n\t\t\t\t\t\t\t\t\tfmAllow  : 1,\n\t\t\t\t\t\t\t\t\tfmOpen : function(callback) {\n\t\t\t\t\t\t\t\t\t\t$(\\\'<div />\\\').dialogelfinder({\n                                                                                        url: \"../core/components/elrte/php/connector.php\",\n\t\t\t\t\t\t\t\t\t\t\tgetFileCallback: callback, // pass callback to file manager\n\t\t\t\t\t\t\t\t\t\t\tcommandsOptions: {\n\t\t\t\t\t\t\t\t\t\t\t\tgetfile: {\n\t\t\t\t\t\t\t\t\t\t\t\t\tonlyURL  : true,\n\t\t\t\t\t\t\t\t\t\t\t\t\t// allow to return multiple files info\n\t\t\t\t\t\t\t\t\t\t\t\t\tmultiple : false,\n\t\t\t\t\t\t\t\t\t\t\t\t\t// allow to return filers info\n\t\t\t\t\t\t\t\t\t\t\t\t\tfolders  : false,\n\t\t\t\t\t\t\t\t\t\t\t\t\t// action after callback (\"\"/\"close\"/\"destroy\")\n\t\t\t\t\t\t\t\t\t\t\t\t\toncomplete : \\\'destroy\\\'\n\t\t\t\t\t\t\t\t\t\t\t\t}\n\t\t\t\t\t\t\t\t\t\t\t}\n\n\t\t\t\t\t\t\t\t\t\t});\n\t\t\t\t\t\t\t\t\t}\t\t\t\t    \n\t\t\t\t\t\t\t\t}\n\t\t\t\t\t\t\t});\n\t\t\t\t\t\t</script>\';\n\t\t\t\t//Inject the initialisation script of elRTE into the MODx resource head tags\n\t\t\t\t$modx->regClientStartupHTMLBlock($output);\n\t\t\t\t//Inject the registration of elRTE into the \'ta\' textarea to the end of the resource body tag\n\t\t\t\t$modx->event->output(\'<script type=\"text/javascript\">Ext.onReady(function(){$(\\\'#ta\\\').elrte(opts);})</script>\');\n\t\t\t  \tprint_r($modx);\n\t\t\t}\n\t  \t\n\t\tbreak;\n}', 0, 'a:3:{s:7:\"plugins\";a:7:{s:4:\"name\";s:7:\"plugins\";s:4:\"desc\";s:65:\"Plugins to load on editor initilisation separated by a comma \',\'.\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";N;s:4:\"area\";s:0:\"\";}s:7:\"toolbar\";a:7:{s:4:\"name\";s:7:\"toolbar\";s:4:\"desc\";